import axios from 'axios'
import React from 'react'
import {USER_LOGIN_FAIL,USER_LOGIN_REQUEST,USER_LOGIN_SUCCESS,USER_LOGOUT} from "../constants/userConstants.js"

export const login = (email, password) => async (dispatch) => {
    try {
        dispatch({
            type:USER_LOGIN_REQUEST
        })

        const config = { //we r sending data to headers in json
            headers: {
                'Content-Type':'application/json'
            }
        }
        const result = await axios.post('/api/users/login', { email, password }, config);

        dispatch({
            type: USER_LOGIN_SUCCESS,
            payload:result.data
        })
        localStorage.setItem('userInfo',JSON.stringify(result.data))
    } catch (error) {
         dispatch({ type: USER_LOGIN_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}

export const logout = () => (dispatch) => {
    localStorage.removeItem('userInfo')
    dispatch({
        type: USER_LOGOUT,  
    })
}
