import axios from 'axios'
import {
    USER_LOGIN_FAIL, USER_LOGIN_REQUEST, USER_LOGIN_SUCCESS,
    USER_LOGOUT, USER_REGISTER_FAIL, USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS,
    USER_DETAILS_FAIL,USER_DETAILS_SUCCESS,USER_DETAILS_REQUEST,USER_UPDATE_REQUEST,
    USER_UPDATE_FAIL, USER_UPDATE_RESET, USER_UPDATE_SUCCESS, USER_DETAILS_RESET, USER_LIST_REQUEST, USER_LIST_SUCCESS, USER_LIST_FAIL,
    USER_LIST_RESET,
    USER_DELETE_REQUEST,
    USER_DELETE_SUCCESS,
    USER_DELETE_FAIL
} from "../constants/userConstants.js"
import {
    ORDER_LIST_RESET
} from '../constants/orderConstants'
export const login = (email, password) => async (dispatch) => {
    try {
        dispatch({
            type:USER_LOGIN_REQUEST
        })

        const config = { //we r sending data to headers in json
            headers: {
                'Content-Type':'application/json'
            }
        }
        const result = await axios.post('/api/users/login', { email, password }, config);

        dispatch({
            type: USER_LOGIN_SUCCESS,
            payload:result.data
        })
        localStorage.setItem('userInfo',JSON.stringify(result.data))
    } catch (error) {
         dispatch({ type: USER_LOGIN_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}

export const logout = () => (dispatch) => {
    localStorage.removeItem('userInfo')
    dispatch({
        type: USER_LOGOUT,  
    })
    dispatch({
        type: USER_DETAILS_RESET,  
    })
    dispatch({
        type: ORDER_LIST_RESET,  
    })
    dispatch({
        type: USER_LIST_RESET,  
    })
}

export const register = (name,email, password) => async (dispatch) => {
    try {
        dispatch({
            type:USER_REGISTER_REQUEST
        })

        const config = { //we r sending data to headers in json
            headers: {
                'Content-Type':'application/json'
            }
        }
        const result = await axios.post('/api/users', { name,email, password }, config);

        dispatch({
            type: USER_REGISTER_SUCCESS,
            payload:result.data
        })
        dispatch({
            type: USER_LOGIN_SUCCESS,
            payload:result.data
        })
        localStorage.setItem('userInfo',JSON.stringify(result.data))
    } catch (error) {
         dispatch({ type: USER_REGISTER_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}


export const getUserDetail = (id) => async (dispatch,getState) => {
    try {
        dispatch({
            type: USER_DETAILS_REQUEST
            
        })
        const {userLogin:{userInfo}}=getState() //get satate of login user
        const config = { //we r sending data to headers in json
            headers: {
                'Content-Type': 'application/json',
                Authorization:`Bearer ${userInfo.token}`
            }
        }
        const result = await axios.get(`/api/users/${id}`, config);

        dispatch({
            type: USER_DETAILS_SUCCESS,
            payload:result.data
        })
       
    } catch (error) {
         dispatch({ type: USER_DETAILS_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}




export const updateUserProfile = (user) => async (dispatch,getState) => {
    try {
        dispatch({
            type: USER_UPDATE_REQUEST
            
        })
        const {userLogin:{userInfo}}=getState() //get state of login user
        const config = { //we r sending data to headers in json
            headers: {
                'Content-Type': 'application/json',
                Authorization:`Bearer ${userInfo.token}`
            }
        }
        const result = await axios.put(`/api/users/profile`,user, config);

        dispatch({
            type: USER_UPDATE_SUCCESS,
            payload:result.data
        })
        dispatch({
            type: USER_LOGIN_SUCCESS,
            payload:result.data
        })
        localStorage.setItem('userInfo',JSON.stringify(result.data))
    } catch (error) {
         dispatch({ type: USER_UPDATE_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}




export const listUsers = () => async (dispatch,getState) => {
    try {
        dispatch({
            type: USER_LIST_REQUEST
            
        })
        const {userLogin:{userInfo}}=getState() //get state of login user
        const config = { //we r sending data to headers in json
            headers: {
                Authorization:`Bearer ${userInfo.token}`
            }
        }
        const result = await axios.get(`/api/users`,config);

        dispatch({
            type: USER_LIST_SUCCESS,
            payload:result.data
        })
       
      
    } catch (error) {
         dispatch({ type: USER_LIST_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}


export const deleteUser = (id) => async (dispatch,getState) => {
    try {
        dispatch({
            type: USER_DELETE_REQUEST
            
        })
        const {userLogin:{userInfo}}=getState() //get state of login user
        const config = { //we r sending data to headers in json
            headers: {
               
                Authorization:`Bearer ${userInfo.token}`
            }
        }
        await axios.delete(`/api/users/${id}`,config);

        dispatch({
            type: USER_DELETE_SUCCESS,
            
        })
       
      
    } catch (error) {
         dispatch({ type: USER_DELETE_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}


export const updateUser = (user) => async (dispatch,getState) => {
    try {
        dispatch({
            type: USER_UPDATE_REQUEST
            
        })
        const {userLogin:{userInfo}}=getState() //get state of login user
        const config = { //we r sending data to headers in json
            headers: {
               'Content-Type':'application/json',
                Authorization:`Bearer ${userInfo.token}`
            }
        }
        const result=await axios.put(`/api/users/${user._id}`,user,config);

        dispatch({
            type: USER_UPDATE_SUCCESS,
           
        })
        dispatch({
            type: USER_DETAILS_SUCCESS,
            payload:result.data
        })
       
      
    } catch (error) {
         dispatch({ type: USER_UPDATE_FAIL,payload:error.response && error.response.data.message?error.response.data.message:error.message }) 
  
    }
}
