import React from 'react'
import {PRODUCT_FAIL,PRODUCT_REQUEST,PRODUCT_SUCCESS,PRODUCT_DETAIL_FAIL,PRODUCT_DETAIL_REQUEST,PRODUCT_DETAIL_SUCCESS, PRODUCT_DELETE_REQUEST, PRODUCT_DELETE_SUCCESS, PRODUCT_DELETE_FAIL } from "../constants/productConstants"
export const productReducer = (state={products:[]},action) => {
    switch (action.type)
    {
        case PRODUCT_REQUEST:
            return { loading: true,products:[] }
        case PRODUCT_SUCCESS:
            return { loading: false, products: action.payload }
        case PRODUCT_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state

    }
}

export const productDetailReducer = (state={product:{reviews:[]}},action) => {
    switch (action.type)
    {
        case PRODUCT_DETAIL_REQUEST:
            return { loading: true,...state }
        case PRODUCT_DETAIL_SUCCESS:
            return { loading: false, product: action.payload }
        case PRODUCT_DETAIL_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state

    }
}

export const deleteProductReducer = (state={},action) => {
    switch (action.type)
    {
        case PRODUCT_DELETE_REQUEST:
            return { loading: true}
        case PRODUCT_DELETE_SUCCESS:
            return { loading: false, success:true }
        case PRODUCT_DELETE_FAIL:
            return { loading: false, error: action.payload }
        default:
            return state

    }
}
