import React,{useState,useEffect} from 'react'
import { listProducts } from "../actions/productActions";
import { Button, Col, Pagination, Row } from 'react-bootstrap';
import { useDispatch, useSelector } from "react-redux"
import Loader from '../components/Loader';
import Product from '../components/Product';
import Message from '../components/Message';
import Paginate from '../components/Paginate';
import Meta from '../components/Meta';
import { Link } from 'react-router-dom';
const ProductFilterScreen = (props) => {
     const keyword = props.match.params.keyword;
    const pageNumber = props.match.params.pageNumber || 1
    const dispatch = useDispatch();
    const productList = useSelector(state => state.productList)
    const { loading, error, products } = productList;
    const[filterProducts,setFilterProducts]=useState([])
    useEffect(() => {
        dispatch(listProducts(keyword,pageNumber))
    }, [dispatch, keyword, pageNumber])
    
    useEffect(() => {
        if (products && products.length >0)
        {   
            
           filterProductData(products)
        }
    }, [products])
    return (
        <>
            <Meta />
            {  filterProducts && filterProducts.length>0 ? filterProducts.map((data, i) => (
                 <>
                    <Row>
                        <Col>
                            <h1 style={{marginBottom:"-20px"}}>{data.category}</h1>
                        </Col>
                        <Col style={{ display: "flex", justifyContent: "flex-end" }}>
                            <Link to={`/products?keyword=${data.category}`}>
                                 <Button type="submit" variant="outline-success" className="p-2 text-dark ">More</Button>
                            </Link>
                           
                        </Col>
                   </Row>
                    
                <hr />
               <Row>
                {
                    data.categoryList.map((ele, i) => (
                        <Col key={ele._id} xs={6} sm={12} md={6} lg={4} xl={3}>
                            <Product product={ele}/>
                        </Col>
                    ))
                }
               
                    </Row>
                    {/* <Paginate pages={pages} page={page} keyword={keyword ? keyword:""} /> */}
                    </>
            )):<h2><Loader /></h2>
               
            }
            
        </>
    )
}

export default ProductFilterScreen
