import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Table,Button,Row,Col} from 'react-bootstrap';
import { useDispatch, useSelector } from "react-redux"
import { listProducts,deleteProduct} from "../actions/productActions"
import Loader from '../components/Loader';
import Message from '../components/Message';
import {LinkContainer} from 'react-router-bootstrap'

const ProductListScreen = (props) => {
      const dispatch = useDispatch();
    const productList = useSelector(state => state.productList)
    const { loading, error, products } = productList;

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin;

    const deleteProductDetail = useSelector(state => state.deleteProductDetail)
    const { loading:deleteLoading,success:deleteSuccess,error:deleteError } = deleteProductDetail;

    

    useEffect(() => {
        if (userInfo && userInfo.isAdmin ) {
            dispatch(listProducts())
        } else {
            props.history.push('/login')
        }
        
    }, [dispatch,props.history,userInfo,deleteSuccess])
    const deleteHandler = (id) => {
        if (window.confirm('Are you sure to delete the user'))
        {
            //delete product
           dispatch(deleteProduct(id))
        }       
        
       
     //  console.log("delete")
    }
    const createProductHandler = () => {
        console.log("create")
    }
    return (
        <>
            <Row className="align-items-center ">
                <Col>
                    <h1>Products</h1>
                </Col>
                <Col className="text-right justify-content-right" >
                    <Button className="my-3 " onClick={createProductHandler}>
                       <i className="fas fa-plus"></i> Create Product
                    </Button>
                </Col>
            </Row>
            {deleteSuccess && <Message variant='success'>Delete successful</Message>}
            {deleteError && <Message variant="warning">{deleteError}</Message>}
            {deleteLoading && <Loader />}
            {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> :
                (
                    <Table striped bordered hover responsive className='table-sm'>
                        <thead>
                            <tr>
                                <td>ID</td>
                                <td>NAME</td>
                                <td>PRICE</td>
                                <td>CATEGORY</td>
                                <td>BRAND</td>
                                <td></td>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                products && products.map(product => (
                                    <tr  key={product._id}>
                                        <td>{product._id}</td>
                                        <td>{product.name}</td>
                                        <td>${product.price}</td>
                                        <td>{product.category}</td>
                                        <td>{product.brand}</td>
                                        
                                        <td>
                                            <LinkContainer to={`/admin/product/${product._id}/edit`}>
                                                <Button variant="light" className="btn-sm">
                                                    <i className="fas fa-edit"></i>
                                                </Button>
                                            </LinkContainer>
                                            <Button variant="danger" className="btn-sm" onClick={() => deleteHandler(product._id)}>
                                                <i className="fas fa-trash"></i>
                                            </Button>
                                        </td>
                                    </tr>
                                ))
                            }
                        </tbody>

                    </Table>
            )
            
            }
        </>
    )
}

export default ProductListScreen
