import React,{useState,useEffect} from 'react'
import { Link } from 'react-router-dom'
import {Row,Col,Image,ListGroup,Card,Button, Form } from "react-bootstrap";
import Rating from '../components/Rating';
import axios from "axios";
import { useDispatch, useSelector } from "react-redux"
import { listSingleProduct,createProductReview } from "../actions/productActions"
import Loader from '../components/Loader';
import Message from '../components/Message';
import {PRODUCT_CREATE_REVIEW_RESET} from '../constants/productConstants'

const ProductScreen = (props) => {
    //const product = products.find((p => p._id === props.match.params.id))
    const dispatch = useDispatch();
    const productDetail = useSelector(state => state.productDetail)
    const { loading, error, product } = productDetail;

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin;

    const productReviewCreate = useSelector(state => state.productReviewCreate)
    const { loading: loadingProductReview , error: errorProductReview , success: successProductReview  } = productReviewCreate;
    
    const [qty, setQty] = useState(1)
    const[rating,setRating]=useState(0)
    const[comment,setComment]=useState('')
    useEffect(() => {
        dispatch(listSingleProduct(props.match.params.id))
    }, [dispatch])
    
    const addToCartHandler = () => {
        props.history.push(`/cart/${props.match.params.id}?qty=${qty}`)
    }

    return (
        <>
            <Link className="btn btn-dark my-3" to="/">Go Back</Link>
            {
                loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message>:(<Row>
                <Col sm={12} md={6} lg={6}>
                        <Image src={product.image} alt={product.name} fluid />
                </Col>
                <Col md={3} >
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h3>{ product.name}</h3>
                        </ListGroup.Item>
                          <ListGroup.Item>
                            <Rating value={product.rating} text={`${product.numReviews} reviews`}/>
                        </ListGroup.Item>
                          <ListGroup.Item>
                           Price: ${product.price}
                        </ListGroup.Item>
                          <ListGroup.Item>
                            Description: {product.description}
                        </ListGroup.Item>
                    </ListGroup>
                </Col>
                <Col md={3}>
                    <Card>
                          <ListGroup>
                              <ListGroup.Item>
                                <Row>
                                    <Col>
                                        Price:
                                    </Col>
                                    <Col>
                                        <strong>${product.price}</strong>
                                    </Col>
                            </Row>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Row>
                                    <Col>
                                        Status:
                                    </Col>
                                    <Col>
                                       {product.countInStock >0 ?"In Stock":"Out of Stock"}
                                    </Col>
                            </Row>
                                </ListGroup.Item>
                                {
                                    product.countInStock > 0 && (
                                        <ListGroup.Item>
                                            <Row>
                                                <Col>
                                                    Qty
                                                </Col>
                                                <Col>
                                                    <Form.Control as='select' value={qty} onChange={(e) => {
                                                    setQty(e.target.value)
                                                }}>
                                                   { [...Array(product.countInStock).keys()].map((x) =>(
                                                     <option key={x + 1} value={x + 1}>{x + 1}</option>
                                                   ))
                                                    }
                                                </Form.Control>
                                                </Col>
                                                
                                            </Row>
                                        </ListGroup.Item>
                                    )
                            }
                            <ListGroup.Item>
                                <Row>
                                <Button className="btn-block " type="button" onClick={addToCartHandler} disabled={product.countInStock ===0}>
                                        Add to Cart
                                </Button>
                                </Row>
                        </ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>
            </Row>
                )
            }
                  </>
    )
}

export default ProductScreen
