import React,{useState,useEffect} from 'react'
import { listProducts } from "../actions/productActions";
import { Button, Col, Pagination, Row ,Form} from 'react-bootstrap';
import { useDispatch, useSelector } from "react-redux"
import Loader from '../components/Loader';
import Product from '../components/Product';
import Message from '../components/Message';
import Paginate from '../components/Paginate';
import Meta from '../components/Meta';
import { Link } from 'react-router-dom';
const ProductFilterScreen = (props) => {
     const keyword = props.match.params.id;
    const pageNumber = props.match.params.pageNumber || 1
    const dispatch = useDispatch();
    const productList = useSelector(state => state.productList)
    const { loading, error, products } = productList;
    const [filterProducts, setFilterProducts] = useState([])
    const [filterAfterDropdown,setFilterAfterDropdown]=useState([])
    const[filterDropdown,setFilterDropDown]=useState('')
    useEffect(() => {
        dispatch(listProducts(keyword,pageNumber))
    }, [dispatch, keyword, pageNumber])
    
    useEffect(() => {
        if (products && products.length >0)
        {
            const filterData=products.filter(ele=> ele.category === keyword)
            console.log("products filter dtata",filterData,keyword)
           setFilterProducts(filterData)
        }
       
    }, [products,filterDropdown])
    useEffect(() => {
         if (filterProducts && filterDropdown)
         {
             if (filterDropdown === 'lowHighPrice')
             {
                const filterDropdownList = filterProducts.sort((a, b) => a.price - b.price)
                setFilterProducts(filterDropdownList)
             }else if (filterDropdown === 'highLowPrice')
             {
                 const filterDropdownList = filterProducts.sort((a, b) => b.price - a.price)
                 setFilterProducts(filterDropdownList)
             } else if (filterDropdown === 'rating')
             {
                const filterDropdownList = filterProducts.sort((a, b) => b.rating - a.rating)
                setFilterProducts(filterDropdownList) 
            }
             
            
    
        }
    },[filterDropdown])
    
    return (
        <>
            <Meta />
            <Row>
                        <Col>
                            <h1 style={{marginBottom:"-20px"}}>Latest {keyword} Products</h1>
                        </Col>
                        <Col style={{ display: "flex", justifyContent: "flex-end" }} md={3}>
                    
                            <Form.Control as='select' value={filterDropdown} onChange={(e) => setFilterDropDown(e.target.value)}>
                                <option key={'none'} value={"none"}>Filter products..</option>
                                <option key={'rating'} value={"rating"}>High Rating</option>
                                <option key={'lprice'} value={"lowHighPrice"}>Low to High</option>
                                <option key={'hprice'} value={"highLowPrice"}>High to Low</option>
                            </Form.Control>
                        </Col>
                   </Row>
           
            <hr />
            {
                loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> :<>
                    <Row>
                {
                   filterProducts && filterProducts.map((ele, i) => (
                        <Col key={ele._id} xs={6} sm={12} md={6} lg={4} xl={3}>
                            <Product product={ele}/>
                        </Col>
                    ))
                }
               
                    </Row>
                    {/* <Paginate pages={pages} page={page} keyword={keyword ? keyword:""} /> */}
                    </>
            }
            
        </>
    )
}

export default ProductFilterScreen
