import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { Form,Button } from 'react-bootstrap';
import { useDispatch, useSelector } from "react-redux"
import { getUserDetail,updateUser } from "../actions/userAction"
import Loader from '../components/Loader';
import Message from '../components/Message';
import FormContainer from '../components/FormContainer';
import {USER_UPDATES_RESET} from "../constants/userConstants"
import MarginalDistanceTop from '../components/MarginalDistanceTop';

const UserEditScreen = (props) => {
    const userId = props.match.params.id;
    const[name,setName]=useState('')
    const [email, setEmail] = useState('')
    const [isAdmin, setIsAdmin] = useState(false)
    
    const dispatch = useDispatch();
    const userDetails = useSelector(state => state.userDetails)
    const { loading, error, user } = userDetails;

    const userUpdate = useSelector(state => state.userUpdate)
    const { loading:loadingUpdate, error:errorUpdate, success:successUpdate } = userUpdate;
    
    useEffect(() => {
        if (successUpdate)
        {
            dispatch({ type: USER_UPDATES_RESET })
            props.history.push('/admin/userlist')
        } else{
             if (user) {
           if (!user.name || user._id !== userId) {
           dispatch(getUserDetail(userId)) 
        } else {
            setName(user.name)
            setEmail(user.email)
            setIsAdmin(user.isAdmin)
        } 
        }
        }
       
        
    },[dispatch,user,successUpdate,userId])
    const submitHandler = (e) => {
        e.preventDefault()
        

        dispatch(updateUser({
            _id: userId,
            name,
            email,
            isAdmin
        }))
        
    }
    return (
        <>
           
            <Link to='/admin/userlist' className="btn btn-dark my-3">Go Back</Link>
            <FormContainer>
            <h1>Edit User</h1>
                {loadingUpdate && <Loader />}
                {errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}
                {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> :
                    <Form onSubmit={submitHandler}>
                        <Form.Group className="my-2">
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type='text'
                                placeholder='Enter name'
                                value={name} onChange={(e) => setName(e.target.value)}>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="my-2">
                            <Form.Label>Email Address</Form.Label>
                            <Form.Control
                                type='email'
                                placeholder='Enter email'
                                value={email} onChange={(e) => setEmail(e.target.value)}>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group controlId="isadmin">
                            
                            <Form.Check
                                type='checkbox'
                                label='Is Admin'
                                checked={isAdmin}
                                onChange={(e) => setIsAdmin(e.target.checked)}>
                            </Form.Check>
                        </Form.Group>
                       
                        <Button type="submit" className="my-2" variant='primary' >Update</Button>
                    </Form>
                }
            </FormContainer>
            </>
       
    )
}

export default UserEditScreen;
