import React,{useState,useEffect} from 'react'
import { useDispatch, useSelector } from "react-redux"
import { addToCart } from "../actions/cartAction"
import {Row,Col,Image,ListGroup,Card,Button, Form } from "react-bootstrap";
import Loader from '../components/Loader';
import Message from '../components/Message';
import { Link } from 'react-router-dom'

const CartScreen = (props) => {
    const productId = props.match.params.id;
    const qty = props.location.search? Number(props.location.search.split('=')[1]):1
    // console.log(qty)
   
     const dispatch = useDispatch();
    const cart = useSelector((state) => state.cart)
    const { cartItems } = cart;
    console.log(cartItems)
    useEffect(() => {
        if (productId)
        {
            dispatch(addToCart(productId, qty))
            
        }
       
    }, [dispatch, productId, qty])
    const removeHandler = (id) => {
        console.log(id)
    }
    return (
        <Row>
            <Col md={8}>
                <h1>Shopping Cart</h1>
                {
                    cartItems.length === 0 ? <Message >Your cart is empty <Link to='/'>Go Back</Link></Message> : <ListGroup variant="flush">
                        {cartItems.map(item => (
                            <ListGroup.Item key={item.product}>
                                <Row>
                                    <Col md={2}>
                                        <Image src={item.image} alt={item.name} fluid rounded/>
                                    </Col>
                                    <Col md={3}>
                                        <Link to={`/product/${item.product}`}>{item.name}</Link>
                                       
                                    </Col>
                                     <Col md={2}>
                                       {item.price}
                                    </Col>
                                     <Col md={2}>
                                       <Form.Control as='select' value={item.qty} onChange={(e) => {
                                                   dispatch(addToCart(item.product,Number(e.target.value)))
                                                }}>
                                                   { [...Array(item.countInStock).keys()].map((x) =>(
                                                     <option key={x + 1} value={x + 1}>{x + 1}</option>
                                                   ))
                                                    }
                                                </Form.Control>
                                    </Col>
                                    <Col md={2}>
                                        <Button type='button' variant='light' onClick={()=>removeHandler(item.product)}><i className="fas fa-trash"></i></Button>
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                }
            </Col>
            <Col md={4}>
                <Card>
                    <ListGroup>
                        <ListGroup.Item>
                            <h3>Subtotal ({cartItems.reduce((acc,next)=>acc +next.qty , 0)}) items</h3>
                        </ListGroup.Item>
                    </ListGroup>
                </Card>
            </Col>
           
        </Row>
    )
}

export default CartScreen
